 package org.opensourcephysics.sip.ch08;// gets needed classes, * means get all classes in controls folder or subdirectoryimport org.opensourcephysics.controls.*; import org.opensourcephysics.display.*;import org.opensourcephysics.frames.*;import java.io.*;public class FilesApp extends AbstractCalculation {   DisplayFrame display = new DisplayFrame("","","");   public void calculate() { // Does a calculation     readFile();   }   public static void main(String[] args) {      // Create a calculation control structure using this class      CalculationControl.createApp(new FilesApp());   }      public void readFile() {      File file = GUIUtils.showOpenDialog(display);      String dir = file.getParent();      try {         BufferedReader br = new BufferedReader(new FileReader(file));         String s = null;         int i = 0;         while ((s = br.readLine())!= null) {            if(s.equals("// start break")) {               s = br.readLine();               s = s.substring(3)+".java";               File outfile = new File(dir,s);               FileWriter fw = new FileWriter(outfile);               PrintWriter pw = new PrintWriter(fw);               s = br.readLine();               while (s.equals("// end break") == false) {                pw.println(s);                s = br.readLine();               }               pw.close();             }         }         br.close();      } catch(IOException e) {}  }}